import React from "react";
import { chatbot, user } from "../../Assets";

export default function Demo() {
  return (
    <div>
      {" "}
      <header>Chat with Chitti</header>
      <section className="main">
        <div className="user-area">
          <img src="images/user.png" />
        </div>
        <div className="chatarea-main">
          <div className="chatarea-outer"></div>
        </div>
        <div class="chatbot-area">
          <img src={chatbot} />
        </div>
        <button id="mic">
          <i className="flaticon-microphone"></i>
        </button>
      </section>
    </div>
  );
}
