import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import "../App.css";
import cardComponent from "./ActionComponents/MainPageCards/MainPageCardComponents";
import { Link } from "react-router-dom";
import Demo from "./ChatBot/bot";

export default function PlaCard() {
  const [words, setWords] = useState("");
  // const [carDiv, setCardDiv] = useState(MediaCard());

  const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });

  function addWord(word) {
    setWords((prev) => prev + " " + word);

    const speech = new SpeechSynthesisUtterance();
    speech.text = "hello";
    window.speechSynthesis.speak(speech);
  }

  return (
    <div>
      <Header words={words} />

      <div className="card-space">
        <div className="card-container">
          <MakeCardDivs name="Lizard" description="This is lizard" />
          {/* {carDiv}
          {carDiv}
          {MediaCard()}
          {MediaCard()}
          {MediaCard()}
          {MediaCard()}
          {MediaCard()}
          {MediaCard()}
          {MediaCard()}
          {MediaCard()} */}
        </div>
      </div>
    </div>
  );

  function MakeCardDivs(props) {
    return (
      <Card
        className="card"
        //   onClick={() => {
        //     setCardDiv(foodCard());
        //     addWord("lizard");
        //   }}
      >
        <CardActionArea>
          <CardMedia
            className="card-media"
            image="https://images.ctfassets.net/hrltx12pl8hq/3MbF54EhWUhsXunc5Keueb/60774fbbff86e6bf6776f1e17a8016b4/04-nature_721703848.jpg?fit=fill&w=480&h=270"
            title="Contemplative Reptile"
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              {props.name}
            </Typography>
            <Typography variant="body2" color="textSecondary" component="p">
              {props.description}
            </Typography>
          </CardContent>
        </CardActionArea>
      </Card>
    );
  }

  // function MediaCard() {
  //   return (
  //     <div className="card-container">
  //       {cardComponent.map((cardInfo) => makeCardDivs(cardInfo))}
  //     </div>
  //   );
  // }

  function onClickMediaCard() {}

  function foodCard() {
    // const classes = useStyles();

    return (
      <Card className="card" onClick={() => addWord("lizard")}>
        <CardActionArea>
          <CardMedia
            // className={classes.media}
            image="https://images.ctfassets.net/hrltx12pl8hq/3MbF54EhWUhsXunc5Keueb/60774fbbff86e6bf6776f1e17a8016b4/04-nature_721703848.jpg?fit=fill&w=480&h=270"
            title="Contemplative Reptile"
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              Food
            </Typography>
            <Typography variant="body2" color="textSecondary" component="p">
              Lizards are a widespread group of squamate reptiles, with over
              6,000 species, ranging across all continents except Antarctica
            </Typography>
          </CardContent>
        </CardActionArea>
      </Card>
    );
  }

  function MediaCard() {
    // const classes = useStyles();

    return (
      <Card
        className="card"
        onClick={() => {
          // setCardDiv(foodCard());
          addWord("lizard");
        }}
      >
        <CardActionArea>
          <CardMedia
            // className={classes.media}
            image="https://images.ctfassets.net/hrltx12pl8hq/3MbF54EhWUhsXunc5Keueb/60774fbbff86e6bf6776f1e17a8016b4/04-nature_721703848.jpg?fit=fill&w=480&h=270"
            title="Contemplative Reptile"
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              Lizard
            </Typography>
            <Typography variant="body2" color="textSecondary" component="p">
              Lizards are a widespread group of squamate reptiles, with over
              6,000 species, ranging across all continents except Antarctica
            </Typography>
          </CardContent>
        </CardActionArea>
      </Card>
    );
  }

  function Header(props) {
    return (
      <div className="header">
        <div className="textHeader">
          <br />
          <br />
          <div>{props.words}</div>
          <br />
          <br />
        </div>
        <button className="cancel" onClick={() => setWords("")}>
          X
        </button>
      </div>
    );
  }
}
