import React,{useState} from "react";

export default function Header(props) {
  const [text, setText] = useState(props.words);

  return (
    <div className="header">
    <div className="textHeader">
      <br />
      <br />
      <div>{text}</div>
      <br />
      <br />
    </div>
    <button className="cancel" onClick={()=> setText("")}>X</button>
    </div>
  );
}
