import React, { useState } from "react";
import chatbot from "../../Assets/chatbot.png";
import user from "../../Assets/user.png";
import "../ChatBot/styles.css";
import axios from "axios";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

let intro = [
  "Hello, I am Chitti",
  "Hi, I am a Robo",
  "Hello, My name is Chitti",
];
let help = [
  "How may i assist you?",
  "How can i help you?",
  "What i can do for you?",
];
let greetings = [
  "i am good you little piece of love",
  "i am fine, what about you",
  "don't want to talk",
  "i am good",
];
let hobbies = [
  "i love to talk with humans",
  "i like to make friends like you",
  "i like cooking",
];
let pizzas = [
  "which type of pizza do you like?",
  "i can make a pizza for you",
  "i would love to make a pizza for you",
  "would you like cheese pizza?",
];
let thank = ["Most welcome", "Not an issue", "Its my pleasure", "Mention not"];
let closing = [
  "Ok bye-bye",
  "As you wish, bye take-care",
  "Bye-bye, see you soon..",
];

export default function Demo() {
  const [divText, setDivText] = useState();
  const [buttonIcon, setButtonIcon] = useState("fas fa-microphone-alt fa-3x");

  var SpeechRecognition =
    window.SpeechRecognition || window.webkitSpeechRecognition;
  var recognition = new SpeechRecognition();

  function showUserMessage(userMessage) {
    const output = <div className="chatarea-inner user">{userMessage}</div>;
    setDivText((prev) => (
      <div>
        {prev}
        {output}
      </div>
    ));
  }

  function showBotMessage(botMessage) {
    const output = <div className="chatarea-inner chatbot">{botMessage}</div>;
    setDivText((prev) => (
      <div>
        {prev}
        {output}
      </div>
    ));
  }

  function chatBotVoice(message) {
    const speech = new SpeechSynthesisUtterance();
    speech.text = "This is test message.";

    if (message.includes("who are you")) {
      let finalResult = intro[Math.floor(Math.random() * intro.length)];
      speech.text = finalResult;
    }

    if (message.includes("fine")) {
      let finalresult = help[Math.floor(Math.random() * help.length)];
      speech.text = finalresult;
    }
    if (message.includes("how are you" || "how are you doing today")) {
      let finalresult = greetings[Math.floor(Math.random() * greetings.length)];
      speech.text = finalresult;
    }
    if (
      message.includes(
        "tell me something about you" || "tell me something about your hobbies"
      )
    ) {
      let finalresult = hobbies[Math.floor(Math.random() * hobbies.length)];
      speech.text = finalresult;
    }
    if (message.includes("pizza")) {
      let finalresult = pizzas[Math.floor(Math.random() * pizzas.length)];
      speech.text = finalresult;
    }
    if (message.includes("thank you" || "thank you so much")) {
      let finalresult = thank[Math.floor(Math.random() * thank.length)];
      speech.text = finalresult;
    }
    if (message.includes("talk to you" || "talk")) {
      let finalresult = closing[Math.floor(Math.random() * closing.length)];
      speech.text = finalresult;
    }

    window.speechSynthesis.speak(speech);
    showBotMessage(speech.text);
  }

  recognition.onresult = function (e) {
    let resultIndex = e.resultIndex;
    let transcript = e.results[resultIndex][0].transcript;
    showUserMessage(transcript);
    chatBotVoice(transcript);
    console.log(transcript);
  };

  recognition.onend = function () {
    setButtonIcon("fas fa-microphone-alt fa-3x");
  };

  function clickIt() {
    setButtonIcon("fas fa-volume-up fa-3x");
    recognition.start();
    console.log("Activated");
  }

  const [meaning, setMeaning] = useState("Meaning appears here ...");
  const [word, setWord] = useState("");

  function getJoke() {
    let url = "https://api.dictionaryapi.dev/api/v2/entries/en_US/" + word;
    axios.get(url).then((res) => {
      // response = res.data[0].id;

      console.log(
        "Here : " + res.data[0].meanings[0].definitions[0].definition
      );

      setMeaning(res.data[0].meanings[0].definitions[0].definition);
    });
  }

  function clear() {
    setWord("");
    setMeaning("Meaning appears here ...");
  }

  return (
    <div>
      {" "}
      <link
        rel="stylesheet"
        href="https://use.fontawesome.com/releases/v5.12.1/css/all.css"
        crossorigin="anonymous"
      ></link>
      <div className="meaning-div">
        <TextField
          id="outlined-basic"
          label="Enter a word"
          variant="outlined"
          placeholder="word"
          value={word}
          onChange={(e) => setWord(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          id="submit"
          onClick={getJoke}
        >
          Get Meaning
        </Button>
        <Button variant="contained" color="primary" id="submit" onClick={clear}>
          Clear
        </Button>
        <Button color="secondary" id="meaning">
          {meaning}
        </Button>
      </div>
      <header>Feeling lonely? Chat with Garima !</header>
      <div className="main">
        <div className="user-area">
          <img src={user} />
        </div>
        <div className="chatarea-main">
          <div className="chatarea-outer">{divText}</div>
        </div>
        <div className="chatbot-area">
          <img src={chatbot} />
        </div>
        <button onClick={clickIt}>
          <i className={buttonIcon}></i>
        </button>
      </div>
    </div>
  );
}
